// src/styles/theme.js
import { keyframes } from 'styled-components';

// Define any global keyframes here if you want them in the theme object
const gradientBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const theme = {
  colors: {
    // --- Bohemian Rhapsody Film Inspired Palette ---
    primary: '#A52A2A',            // Brown (Warm, Vintage)
    primaryLight: '#CD5C5C',       // Indian Red (Slightly lighter, for accents)
    primaryAccent: '#B8860B',      // Dark Goldenrod (Opulent, Theatrical)
    primaryDark: '#800000',        // Dark Maroon (Deeper, richer feel)

    secondary: '#556B2F',          // Dark Olive Green (Subtle, earthy contrast)
    secondaryLight: '#8FBC8F',     // Dark Sea Green (Lighter, for subtle highlights)
    secondaryDark: '#33421E',      // Very Dark Olive Green

    info: '#F4A460',               // Sandy Brown (Warm, inviting details)
    success: '#2E8B57',            // Sea Green (Classic success)
    warning: '#DAA520',            // Goldenrod (Highlights, important elements)
    danger: '#DC143C',             // Crimson (Strong warnings/errors)

    // --- Backgrounds & Text (Warm & Textured) ---
    background: '#221F1F',         // Dark Charcoal/Brown (Subtle texture)
    backgroundDark: '#191717',     // Even Darker for depth
    dark: '#191717',               // Consistent dark for cards/sections
    light: '#F5F5DC',              // Beige (Warm, vintage light tone)

    text: '#E0D8B7',               // Light Beige (Readable on darks)
    textLight: '#FFFFFF',          // Pure White (Headings, high contrast)
    textSecondary: '#A09070',      // Muted Beige (Subtle text)

    border: '#8B4513',             // Saddle Brown (Warm, subtle border)
    focus: '#B8860B',              // Dark Goldenrod for focus states
    active: '#CD5C5C',             // Indian Red for active states

    // --- Vintage/Theatrical Gradients ---
    gradientPrimary: 'linear-gradient(135deg, #A52A2A 0%, #B8860B 100%)', // Brown to Dark Goldenrod
    gradientSecondary: 'linear-gradient(135deg, #556B2F 0%, #F4A460 100%)', // Dark Olive Green to Sandy Brown
    gradientVibrant: 'linear-gradient(45deg, #A52A2A, #556B2F, #B8860B, #F4A460)', // Multi-stop, warm and rich
  },
  spacing: {
    xxs: '4px',
    xs: '8px',
    sm: '12px',
    md: '16px',
    lg: '24px',
    xl: '32px',
    xxl: '48px',
    xxxl: '64px',
  },
  fonts: {
    main: 'Roboto, sans-serif',
    heading: 'Montserrat, sans-serif',
    display: 'Oswald, sans-serif',
  },
  breakpoints: {
    mobile: '576px',
    tablet: '768px',
    desktop: '992px',
    lg: '1200px',
  },
  shadows: {
    sm: '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)',
    md: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
    lg: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
    xl: '0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22)',
    xxl: '0 19px 38px rgba(0,0,0,0.3), 0 15px 12px rgba(0,0,0,0.22)',
    inset: 'inset 0 2px 4px rgba(0,0,0,0.2)',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    xl: '20px',
    full: '9999px',
  },
  transitions: {
    fast: '0.15s',
    normal: '0.3s',
    slow: '0.5s',
    ease: 'ease',
    easeIn: 'ease-in',
    easeOut: 'ease-out',
    easeInOut: 'ease-in-out',
    easeOutQuint: 'cubic-bezier(0.23, 1, 0.32, 1)',
  },
  animations: {
    fadeInDuration: '0.8s',
    slideInDuration: '1s',
    blobDanceDuration: '30s',
    gradientCycleDuration: '25s',
    delaySm: '0.1s',
    delayMd: '0.2s',
    delayLg: '0.4s',
    gradientBackground: gradientBackground,
  },
  zIndex: {
    base: 100,
    dropdown: 1000,
    sticky: 1100,
    modal: 1200,
    overlay: 1300,
    loader: 1400,
  },
};

export default theme;
