    filter: blur(150px); /* More blur for ethereal look */
    opacity: 0.2; /* Subtle transparency */
    animation: ${blobDance} 45s ease-in-out infinite alternate, rotateBlob1 60s linear infinite;
    transform-origin: 70% 30%; /* Different origin for rotation */
  }

  body::after {
    bottom: -35vh; /* Positioned from bottom */
    right: -35vw;  /* Positioned from right */
    width: 200vw; /* Even larger blob */
    height: 200vh; /* Even larger blob */
    background: radial-gradient(
      circle at 80% 20%,
      ${({ theme }) => theme.colors.secondaryLight} 0%,
      transparent 60%
    );
    filter: blur(180px); /* Even more blur */
    opacity: 0.15; /* More subtle */
    animation: ${blobDance} 50s ease-in-out infinite reverse, rotateBlob2 70s linear infinite;
    transform-origin: 20% 80%; /* Different origin for rotation */
    animation-delay: 5s; /* Stagger this blob */
  }

  /* Distant starfield/dust layer (most background layer) */
  body::before { /* Re-using :before, but as another layer. Better to use a dedicated element if HTML allows. */
    /* This will be the very subtle background layer */
    content: '';
    position: fixed;
    top: 0; left: 0; right: 0; bottom: 0;
    background-image: radial-gradient(
      circle at 50% 50%,
      rgba(255, 255, 255, 0.05) 1px,
      transparent 1px
    );
    background-size: 100px 100px; /* Density of "stars" */
    animation: ${starfieldMove} 300s linear infinite; /* Very slow, long movement */
    z-index: -2; /* Lowest layer */
    opacity: 0.5; /* Subtle */
  }


  /* Keyframes for subtle rotation to enhance 3D feel */
  @keyframes rotateBlob1 {
    0% { transform: rotate(0deg) rotateX(0deg); }
    100% { transform: rotate(360deg) rotateX(20deg); }
  }

  @keyframes rotateBlob2 {
    0% { transform: rotate(0deg) rotateY(0deg); }
    100% { transform: rotate(360deg) rotateY(15deg); }
  }


  /* General typography and element styling (kept from previous versions for consistency) */
  h1, h2, h3, h4, h5, h6 {
    font-family: ${({ theme }) => theme.fonts.heading};
    color: var(--color-text-light);
    margin-bottom: var(--spacing-md);
    line-height: 1.2;
    font-weight: 700; /* Bolder headings */
    text-shadow: 2px 2px 4px rgba(0,0,0,0.15); /* More prominent shadow */

    /* Text Clip Reveal for main headings */
    &.clip-reveal {
      overflow: hidden;
      display: inline-block; /* Essential for clip-path on text */
      clip-path: inset(0 100% 0 0); /* Start hidden */
      animation: ${textClipReveal} ${({ theme }) => theme.animations.slideInDuration} ${({ theme }) => theme.transitions.easeOutQuint} forwards;
      animation-delay: ${({ theme }) => theme.animations.delayMd};
    }
  }

  h1 { font-size: 3.5rem; font-family: ${({ theme }) => theme.fonts.display}; }
  h2 { font-size: 2.8rem; }
  h3 { font-size: 2.2rem; }
  h4 { font-size: 1.8rem; }
  h5 { font-size: 1.4rem; }
  h6 { font-size: 1.1rem; }

  /* Links */
  a {
    text-decoration: none;
    color: var(--color-primary);
    transition: all 0.3s cubic-bezier(0.65, 0.05, 0.36, 1);
    position: relative;
    display: inline-block;

    &:hover {
      color: var(--color-primary-accent);
      transform: translateY(-4px) scale(1.03);
      filter: drop-shadow(0 4px 8px rgba(0, 123, 255, 0.4));
    }
    &:active {
      color: var(--color-active);
      transform: translateY(0) scale(1);
    }
    &:focus {
      outline: 2px solid var(--color-focus);
      outline-offset: 4px;
      border-radius: var(--spacing-xxs);
      animation: ${borderGlow} 1s infinite alternate;
    }

    /* Animated underline */
    &::after {
      content: '';
      position: absolute;
      width: 0;
      height: 2px;
      bottom: -4px;
      left: 0;
      background-color: var(--color-secondary);
      transition: width 0.4s cubic-bezier(0.65, 0.05, 0.36, 1);
    }
    &:hover::after {
      width: 100%;
    }
  }

  /* Buttons */
  button {
    font-family: inherit;
    cursor: pointer;
    border: none;
    padding: var(--spacing-md) var(--spacing-xl);
    border-radius: ${({ theme }) => theme.borderRadius.xl};
    background: linear-gradient(135deg, var(--color-primary) 0%, var(--color-primary-accent) 100%);
    color: var(--color-text-light);
    font-weight: 700;
    letter-spacing: 1px;
    box-shadow: ${({ theme }) => theme.shadows.lg};
    transition: all 0.4s cubic-bezier(0.65, 0.05, 0.36, 1);
    text-transform: uppercase;
    position: relative;
    overflow: hidden;
    will-change: transform, box-shadow, background-color;

    &:hover {
      background: linear-gradient(135deg, var(--color-primary-accent) 0%, var(--color-primary) 100%);
      transform: translateY(-6px) scale(1.03);
      box-shadow: ${({ theme }) => theme.shadows.xxl};
      filter: brightness(1.2);
    }
    &:active {
      background: var(--color-primary-dark);
      transform: translateY(0);
      box-shadow: ${({ theme }) => theme.shadows.inset};
    }
    &:focus {
      outline: none;
      box-shadow: 0 0 0 4px var(--color-focus), ${({ theme }) => theme.shadows.lg};
      animation: ${borderGlow} 1s infinite alternate;
    }

    /* Grand Ripple Effect on Click/Tap */
    .ripple-effect {
      position: absolute;
      border-radius: 50%;
      background: rgba(255, 255, 255, 0.7);
      animation: ${ripple} 0.6s linear forwards;
      pointer-events: none;
      transform: scale(0);
      opacity: 0;
    }

    /* Secondary Button Style */
    &.secondary {
      background: linear-gradient(135deg, var(--color-secondary) 0%, var(--color-secondary-light) 100%);
      &:hover {
        background: linear-gradient(135deg, var(--color-secondary-light) 0%, var(--color-secondary) 100%);
        filter: brightness(1.2);
      }
      &:active {
        background: var(--color-secondary-dark);
      }
    }

    /* Ghost Button Style */
    &.ghost {
      background: transparent;
      color: var(--color-primary);
      border: 2px solid var(--color-primary);
      box-shadow: none;
      &:hover {
        background: var(--color-primary);
        color: var(--color-text-light);
        box-shadow: ${({ theme }) => theme.shadows.sm};
        transform: translateY(-2px);
      }
      &:active {
        background: var(--color-primary-dark);
        border-color: var(--color-primary-dark);
      }
    }

    /* Animated entry for buttons */
    &.animated-button {
      animation: ${fadeInScale} 1s cubic-bezier(0.23, 1, 0.32, 1) forwards;
      opacity: 0;
    }
  }

  /* Form Elements */
  input, textarea, select {
    padding: var(--spacing-sm);
    border: 1px solid var(--color-border);
    border-radius: var(--spacing-md);
    font-family: inherit;
    font-size: 1rem;
    color: var(--color-text);
    background-color: var(--color-dark);
    transition: all 0.3s ease-in-out;
    box-shadow: ${({ theme }) => theme.shadows.sm};

    &:focus {
      outline: none;
      border-color: var(--color-primary-accent);
      box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.2), ${({ theme }) => theme.shadows.md};
      transform: scale(1.01);
    }
    &::placeholder {
      color: var(--color-text-secondary);
      opacity: 0.7;
    }
    &:hover {
      border-color: var(--color-text-secondary);
      box-shadow: ${({ theme }) => theme.shadows.md};
    }
  }

  textarea {
    resize: vertical;
    min-height: 100px;
  }

  /* Images & Media */
  img {
    max-width: 100%;
    height: auto;
    display: block;
    object-fit: cover;
    border-radius: var(--spacing-md);
    transition: transform 0.4s cubic-bezier(0.23, 1, 0.32, 1),
                box-shadow 0.4s cubic-bezier(0.23, 1, 0.32, 1);

    &:hover {
      transform: scale(1.05);
      box-shadow: ${({ theme }) => theme.shadows.lg};
    }

    /* Animated image border (subtle) */
    &.bordered-animated-image {
      border: 4px solid transparent;
      background-clip: padding-box;
      background-origin: border-box;
      background-image: linear-gradient(45deg, var(--color-primary), var(--color-secondary), var(--color-info));
      animation: ${gradientBackground} 4s linear infinite;
      background-size: 200% 200%;
    }

    /* Example: Apply float to an image */
    &.floating-image {
        animation: ${float} 3s ease-in-out infinite alternate;
    }
  }

  /* Utility Classes */
  .container {
    max-width: 1280px;
    margin: 0 auto;
    padding: 0 var(--spacing-lg);
    z-index: 1;
    position: relative;
  }

  .text-center {
    text-align: center;
  }

  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  /* Interactive Cards/Elements */
  .card {
    background-color: var(--color-dark);
    border-radius: ${({ theme }) => theme.borderRadius.xl};
    box-shadow: ${({ theme }) => theme.shadows.md};
    padding: var(--spacing-xl);
    margin-bottom: var(--spacing-xxl);
    perspective: 1000px; /* For 3D tilt effect */
    transform-style: preserve-3d;
    transition: all 0.4s cubic-bezier(0.23, 1, 0.32, 1);
    will-change: transform, box-shadow;
    color: var(--color-text); /* Ensure card text is visible */

    &:hover {
      transform: translateY(-12px) rotateX(7deg) rotateY(-7deg); /* More dramatic lift and tilt */
      box-shadow: ${({ theme }) => theme.shadows.xxl}; /* Grandest shadow on hover */
      position: relative;
      z-index: 2; /* Bring hovered card slightly forward */
    }
    &:active {
      transform: translateY(-4px) rotateX(0deg) rotateY(0deg);
      box-shadow: ${({ theme }) => theme.shadows.lg};
    }

    &.animated-card {
      animation: ${fadeInScale} 1.2s cubic-bezier(0.23, 1, 0.32, 1) forwards;
      opacity: 0;
      transform: scale(0.9) translateY(20px);
    }
  }

  /* Placeholder for Loading States / Skeleton Screens */
  .skeleton-line {
    background-color: var(--color-border);
    border-radius: var(--spacing-xxs);
    height: 1rem;
    margin-bottom: var(--spacing-sm);
    width: 100%;
    overflow: hidden;
    position: relative;
    &::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
      animation: ${shimmer} 1.5s infinite linear;
    }
  }

  /* Scroll-to-Top Button or other fixed elements */
  .fixed-bottom-right {
    position: fixed;
    bottom: var(--spacing-xl);
    right: var(--spacing-xl);
    z-index: 100;
    animation: ${bounce} 2s infinite cubic-bezier(0.68, -0.55, 0.27, 1.55);
    box-shadow: ${({ theme }) => theme.shadows.lg};
    border-radius: 50%;
    width: 50px;
    height: 50px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 1.5rem;
    background-color: var(--color-primary);
    color: var(--color-text-light);
    transition: background-color 0.3s ease, transform 0.3s ease;
    &:hover {
      background-color: var(--color-primary-accent);
      transform: translateY(-5px);
    }
  }

  /* Animated Scroll Indicator (if you're using this in your App.js) */
  .scroll-indicator-bar {
    position: fixed;
    top: 0;
    left: 0;
    height: 5px;
    background-color: var(--color-info);
    width: 0%;
    z-index: 9999;
    transition: width 0.1s linear;
  }

  /* Custom Scrollbar */
  ::-webkit-scrollbar {
    width: 12px;
    height: 12px;
  }
  ::-webkit-scrollbar-track {
    background: var(--color-background);
    border-radius: 10px;
  }
  ::-webkit-scrollbar-thumb {
    background: var(--color-primary-accent);
    border-radius: 10px;
    border: 3px solid var(--color-background);
    &:hover {
      background: var(--color-primary);
    }
  }

  /* Media Queries for Responsive Design */
  @media (max-width: ${({ theme }) => theme.breakpoints.desktop}) {
    h1 { font-size: 3rem; }
    h2 { font-size: 2.4rem; }
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.tablet}) {
    h1 { font-size: 2.5rem; }
    h2 { font-size: 2rem; }
    h3 { font-size: 1.8rem; }
    body::before, body::after {
      width: 100vw; /* Smaller on tablets */
      height: 100vh;
      filter: blur(80px);
    }
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.mobile}) {
    html { font-size: 15px; }
    h1 { font-size: 2rem; }
    h2 { font-size: 1.75rem; }
    button {
      padding: var(--spacing-sm) var(--spacing-md);
      font-size: 0.9rem;
    }
    .fixed-bottom-right {
      bottom: var(--spacing-md);
      right: var(--spacing-md);
      width: 40px;
      height: 40px;
      font-size: 1.2rem;
    }
  }
`;

export default GlobalStyle;
