// src/styles/theme.js
import { keyframes } from 'styled-components';

// Define any global keyframes here if you want them in the theme object
const gradientBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const theme = {
  colors: {
    // --- "Bohemian Rhapsody" Poster Inspired Palette ---
    primary: '#5B0099',            // Deep Purple (Top of Poster)
    primaryLight: '#8A2BE2',       // Blue Violet (Transition Purple)
    primaryAccent: '#FFD700',      // Pure Gold (Poster Title / Highlights)
    primaryDark: '#3D0066',        // Even Darker Purple

    secondary: '#FFA500',          // Bright Orange (Bottom of Poster)
    secondaryLight: '#FFC107',     // Amber (Lighter Orange for accents)
    secondaryDark: '#CC8400',      // Darker Orange

    info: '#FF4500',               // OrangeRed (For alerts/info that pop)
    success: '#228B22',            // Forest Green (Classic success)
    warning: '#FFD700',            // Pure Gold (Consistent warning/highlight)
    danger: '#DC143C',             // Crimson Red (Strong warnings/errors)

    // --- Backgrounds & Text (Dark & Luminous) ---
    background: '#1A1A1A',         // Very Dark Charcoal (Base background)
    backgroundDark: '#101010',     // Deepest Black (For true depth)
    dark: '#181818',               // Slightly lighter dark for cards/sections
    light: '#F5F5DC',              // Beige (Soft light tone for contrast elements)

    text: '#D0D0C0',               // Warm Light Gray (General text on darks)
    textLight: '#FFD700',          // Gold (Headings, high contrast text)
    textSecondary: '#A0A090',      // Muted Warm Gray (Subtle text)

    border: '#4A0080',             // Darker Purple (Subtle borders)
    focus: '#FFD700',              // Gold for focus states
    active: '#8A2BE2',             // Blue Violet for active states

    // --- Iconic Poster Gradients ---
    gradientPrimary: 'linear-gradient(135deg, #5B0099 0%, #FFA500 100%)', // Purple to Orange
    gradientSecondary: 'linear-gradient(135deg, #8A2BE2 0%, #FF4500 100%)', // Blue Violet to OrangeRed
    gradientVibrant: 'linear-gradient(45deg, #5B0099, #8A2BE2, #FF4500, #FFD700)', // Multi-stop, dramatic
  },
  spacing: {
    xxs: '4px',
    xs: '8px',
    sm: '12px',
    md: '16px',
    lg: '24px',
    xl: '32px',
    xxl: '48px',
    xxxl: '64px',
  },
  fonts: {
    main: 'Roboto, sans-serif',
    heading: 'Montserrat, sans-serif',
    display: 'Oswald, sans-serif',
  },
  breakpoints: {
    mobile: '576px',
    tablet: '768px',
    desktop: '992px',
    lg: '1200px',
  },
  shadows: {
    sm: '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)',
    md: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
    lg: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
    xl: '0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22)',
    xxl: '0 19px 38px rgba(0,0,0,0.3), 0 15px 12px rgba(0,0,0,0.22)',
    inset: 'inset 0 2px 4px rgba(0,0,0,0.2)',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    xl: '20px',
    full: '9999px',
  },
  transitions: {
    fast: '0.15s',
    normal: '0.3s',
    slow: '0.5s',
    ease: 'ease',
    easeIn: 'ease-in',
    easeOut: 'ease-out',
    easeInOut: 'ease-in-out',
    easeOutQuint: 'cubic-bezier(0.23, 1, 0.32, 1)',
  },
  animations: {
    fadeInDuration: '0.8s',
    slideInDuration: '1s',
    blobDanceDuration: '30s',
    gradientCycleDuration: '25s',
    delaySm: '0.1s',
    delayMd: '0.2s',
    delayLg: '0.4s',
    gradientBackground: gradientBackground,
  },
  zIndex: {
    base: 100,
    dropdown: 1000,
    sticky: 1100,
    modal: 1200,
    overlay: 1300,
    loader: 1400,
  },
};

export default theme;
