// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider } from 'styled-components';
import GlobalStyle from './styles/globalStyles';
import theme from './styles/theme'; // Ensure this path is correct based on your file structure
import ParticleBackground from './components/ParticleBackground';
import Header from './components/Header/Header';
import Footer from './components/Footer/Footer';
import HomePage from './pages/Home/HomePage';
import SearchPage from './pages/Search/SearchPage';
import RecognizePage from './pages/Recognize/RecognizePage';
import LoginPage from './pages/Auth/LoginPage';
import RegisterPage from './pages/Auth/RegisterPage';
import ProfilePage from './pages/Profile/ProfilePage';
import SongDetailPage from './pages/Songs/SongDetailPage';
import PrivateRoute from './components/PrivateRoute';
import { AuthProvider } from './context/AuthContext';
import { AudioProvider } from './context/AudioContext';

function App() {
  return (
    <ThemeProvider theme={theme}>
      {/* AuthProvider and AudioProvider wrap the entire application for context */}
      <AuthProvider>
        <AudioProvider>
          {/* Router manages navigation */}
          <Router>
            {/* GlobalStyle applies base CSS rules to the entire app */}
            <GlobalStyle />

            {/* ParticleBackground renders the live animated particles behind everything */}
            {/* It receives the 'theme' prop to use theme colors for particles */}
            <ParticleBackground theme={theme} />

            {/* Header, Main content (Routes), and Footer are your main UI elements */}
            <Header />
            <main>
              <Routes>
                {/* Public routes */}
                <Route index element={<HomePage />} />
                <Route path="search" element={<SearchPage />} />
                <Route path="login" element={<LoginPage />} />
                <Route path="register" element={<RegisterPage />} />
                <Route path="songs/:id" element={<SongDetailPage />} />

                {/* Protected routes using PrivateRoute */}
                <Route
                  path="recognize"
                  element={
                    <PrivateRoute>
                      <RecognizePage />
                    </PrivateRoute>
                  }
                />
                <Route
                  path="profile"
                  element={
                    <PrivateRoute>
                      <ProfilePage />
                    </PrivateRoute>
                  }
                />
              </Routes>
            </main>
            <Footer />
          </Router>
        </AudioProvider>
      </AuthProvider>
    </ThemeProvider>
  );
}

export default App;
