// In backend/frontend/src/pages/SearchPage/SearchPage.js

// ... (existing imports and code)

<ResultsGrid>
{results && Array.isArray(results) && results.map((song) => (
  // --- FIX for Blank Page on Click and Link console error ---
  // Use 'href' for external Last.fm URLs, and ensure 'as="a"' if SongCard is styled.a
  <SongCard
    key={song._id} // Keep for React list rendering
    as="a" // Explicitly render SongCard as an <a> tag
    href={song.url} // Link directly to the Last.fm URL
    target="_blank" // Open in a new tab
    rel="noopener noreferrer" // Security best practice
  >
    <img src={song.albumCover} alt={song.title} />
    <h4>{song.title}</h4>
    <p>{song.artist}</p>
  </SongCard>
))}
</ResultsGrid>

