// src/styles/theme.js
import { keyframes } from 'styled-components';

// Define any global keyframes here if you want them in the theme object
const gradientBackground = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const theme = {
  colors: {
    // --- Jojo-inspired Primary & Accent Colors ---
    primary: '#7B008B',            // Deep Royal Purple (like a Stand)
    primaryLight: '#A93EC1',       // Lighter, vibrant purple
    primaryAccent: '#CD00FF',      // Electric Magenta/Violet (for glows/accents)
    primaryDark: '#570062',        // Deeper, brooding purple

    secondary: '#FF2D97',          // Hot Pink (very Jojo!)
    secondaryLight: '#FF6BBF',     // Lighter, softer pink
    secondaryDark: '#C70072',      // Darker, intense pink

    info: '#00F0FF',               // Electric Cyan/Aqua (for striking highlights)
    success: '#39FF14',            // Electric Green (for success states)
    warning: '#FFD700',            // GOLD (Crucial for Jojo Stands/special effects!)
    danger: '#FF4500',             // Vibrant Orange Red (for warnings/errors)

    // --- Backgrounds & Text ---
    background: '#1C0F2A',         // Very Dark Saturated Purple (instead of plain blue-black)
    backgroundDark: '#120A1D',     // Even Darker Purple for deeper sections
    dark: '#120A1D',               // For cards/sections, consistent with backgroundDark
    light: '#F8F8F8',              // Near White (for general light elements)

    text: '#E0E0E0',               // Light Gray text (readable on darks)
    textLight: '#FFFFFF',          // Pure White text (for headings, high contrast)
    textSecondary: '#909090',      // Muted text for subtle elements

    border: '#6F4A87',             // Muted purple border, or could be info for highlights
    focus: '#FFD700',              // GOLD focus outline (Jojo style!)
    active: '#FF2D97',             // Hot Pink for active states

    // --- Bizarre Gradients ---
    gradientPrimary: 'linear-gradient(135deg, #7B008B 0%, #FF2D97 100%)', // Purple to Pink
    gradientSecondary: 'linear-gradient(135deg, #FF2D97 0%, #FFD700 100%)', // Pink to Gold
    gradientVibrant: 'linear-gradient(45deg, #7B008B, #FF2D97, #00F0FF, #FFD700)', // Multi-color bizarre gradient!
  },
  spacing: {
    xxs: '4px',
    xs: '8px',
    sm: '12px',
    md: '16px',
    lg: '24px',
    xl: '32px',
    xxl: '48px',
    xxxl: '64px',
  },
  fonts: {
    main: 'Roboto, sans-serif',
    heading: 'Montserrat, sans-serif',
    display: 'Oswald, sans-serif',
  },
  breakpoints: {
    mobile: '576px',
    tablet: '768px',
    desktop: '992px',
    lg: '1200px',
  },
  shadows: {
    sm: '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)',
    md: '0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23)',
    lg: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
    xl: '0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22)',
    xxl: '0 19px 38px rgba(0,0,0,0.3), 0 15px 12px rgba(0,0,0,0.22)',
    inset: 'inset 0 2px 4px rgba(0,0,0,0.2)',
  },
  borderRadius: {
    sm: '4px',
    md: '8px',
    lg: '12px',
    xl: '20px',
    full: '9999px',
  },
  transitions: {
    fast: '0.15s',
    normal: '0.3s',
    slow: '0.5s',
    ease: 'ease',
    easeIn: 'ease-in',
    easeOut: 'ease-out',
    easeInOut: 'ease-in-out',
    easeOutQuint: 'cubic-bezier(0.23, 1, 0.32, 1)',
  },
  animations: {
    fadeInDuration: '0.8s',
    slideInDuration: '1s',
    blobDanceDuration: '30s',
    gradientCycleDuration: '25s',
    delaySm: '0.1s',
    delayMd: '0.2s',
    delayLg: '0.4s',
    gradientBackground: gradientBackground,
  },
  zIndex: {
    base: 100,
    dropdown: 1000,
    sticky: 1100,
    modal: 1200,
    overlay: 1300,
    loader: 1400,
  },
};

export default theme;
