  }

  /* Headings */
  h1, h2, h3, h4, h5, h6 {
    font-family: ${({ theme }) => theme.fonts.heading};
    color: ${({ theme }) => theme.colors.primaryDark};
    margin-bottom: ${({ theme }) => theme.spacing.md};
    line-height: 1.2;
    font-weight: 700; /* Bolder headings */
    text-shadow: 2px 2px 4px rgba(0,0,0,0.15); /* More prominent shadow */

    /* Text Clip Reveal for main headings */
    &.clip-reveal {
      overflow: hidden;
      display: inline-block; /* Essential for clip-path on text */
      clip-path: inset(0 100% 0 0); /* Start hidden */
      animation: ${textClipReveal} ${({ theme }) => theme.animations.slideInDuration} ${({ theme }) => theme.transitions.easeOutQuint} forwards;
      animation-delay: ${({ theme }) => theme.animations.delayMd};
    }
  }

  h1 { font-size: 3.5rem; font-family: ${({ theme }) => theme.fonts.display}; }
  h2 { font-size: 2.8rem; }
  h3 { font-size: 2.2rem; }
  h4 { font-size: 1.8rem; }
  h5 { font-size: 1.4rem; }
  h6 { font-size: 1.1rem; }

  /* Links */
  a {
    text-decoration: none;
    color: ${({ theme }) => theme.colors.primary};
    transition: all ${({ theme }) => theme.transitions.normal} ${({ theme }) => theme.transitions.easeOutQuint};
    position: relative;
    display: inline-block; /* Necessary for some underline effects */

    &:hover {
      color: ${({ theme }) => theme.colors.primaryAccent};
      transform: translateY(-4px) scale(1.03); /* More pronounced lift and scale */
      filter: drop-shadow(0 4px 8px rgba(0, 123, 255, 0.4)); /* Stronger glow */
    }
    &:active {
      color: ${({ theme }) => theme.colors.active};
      transform: translateY(0) scale(1);
    }
    &:focus {
      outline: 2px solid ${({ theme }) => theme.colors.focus};
      outline-offset: 4px;
      border-radius: ${({ theme }) => theme.borderRadius.sm};
      animation: ${borderGlow} 1s infinite alternate; /* Grand glow on focus */
    }

    /* Animated underline */
    &::after {
      content: '';
      position: absolute;
      width: 0;
      height: 2px;
      bottom: -4px;
      left: 0;
      background-color: ${({ theme }) => theme.colors.secondary};
      transition: width ${({ theme }) => theme.transitions.normal} ${({ theme }) => theme.transitions.easeOutQuint};
    }
    &:hover::after {
      width: 100%;
    }
  }

  /* Buttons */
  button {
    font-family: inherit;
    cursor: pointer;
    border: none;
    padding: ${({ theme }) => theme.spacing.md} ${({ theme }) => theme.spacing.xl}; /* More generous padding */
    border-radius: ${({ theme }) => theme.borderRadius.xl}; /* Very rounded buttons */
    background: ${({ theme }) => theme.colors.gradientPrimary};
    color: ${({ theme }) => theme.colors.light};
    font-weight: 700;
    letter-spacing: 1px; /* More spacing */
    box-shadow: ${({ theme }) => theme.shadows.lg}; /* Deeper initial shadow */
    transition: all ${({ theme }) => theme.transitions.normal} ${({ theme }) => theme.transitions.easeOutQuint};
    text-transform: uppercase;
    position: relative;
    overflow: hidden; /* For ripple effect */
    will-change: transform, box-shadow, background-color; /* Optimize animations */

    &:hover {
      background: ${({ theme }) => theme.colors.gradientPrimary}; /* Consistent gradient on hover */
      transform: translateY(-6px) scale(1.03); /* Grand lift and scale */
      box-shadow: ${({ theme }) => theme.shadows.xxl}; /* Most prominent shadow on hover */
      filter: brightness(1.2); /* Brighter on hover */
    }
    &:active {
      background: ${({ theme }) => theme.colors.primaryDark};
      transform: translateY(0);
      box-shadow: ${({ theme }) => theme.shadows.inset};
    }
    &:focus {
      outline: none; /* Custom focus styling */
      box-shadow: 0 0 0 4px ${({ theme }) => theme.colors.focus}, ${({ theme }) => theme.shadows.lg}; /* Combined shadow and outline */
      animation: ${borderGlow} 1s infinite alternate;
    }

    /* Grand Ripple Effect on Click/Tap */
    .ripple-effect {
      position: absolute;
      border-radius: 50%;
      background: rgba(255, 255, 255, 0.7); /* Brighter ripple */
      animation: ${ripple} 0.6s linear forwards;
      pointer-events: none; /* So it doesn't block button clicks */
      transform: scale(0);
      opacity: 0;
    }

    /* Secondary Button Style */
    &.secondary {
      background: ${({ theme }) => theme.colors.gradientSecondary};
      &:hover {
        background: ${({ theme }) => theme.colors.gradientSecondary};
        filter: brightness(1.2);
      }
      &:active {
        background: ${({ theme }) => theme.colors.secondaryDark};
      }
    }

    /* Ghost Button Style */
    &.ghost {
      background: transparent;
      color: ${({ theme }) => theme.colors.primary};
      border: 2px solid ${({ theme }) => theme.colors.primary};
      box-shadow: none;
      &:hover {
        background: ${({ theme }) => theme.colors.primary};
        color: ${({ theme }) => theme.colors.light};
        box-shadow: ${({ theme }) => theme.shadows.sm};
        transform: translateY(-2px);
      }
      &:active {
        background: ${({ theme }) => theme.colors.primaryDark};
        border-color: ${({ theme }) => theme.colors.primaryDark};
      }
    }

    /* Animated entry for buttons */
    &.animated-button {
      animation: ${fadeInScale} ${({ theme }) => theme.animations.fadeInDuration} ${({ theme }) => theme.transitions.easeOutQuint} forwards;
      opacity: 0; /* Start hidden */
    }
  }

  /* Form Elements */
  input, textarea, select {
    padding: ${({ theme }) => theme.spacing.sm};
    border: 1px solid ${({ theme }) => theme.colors.border};
    border-radius: ${({ theme }) => theme.borderRadius.md}; /* More rounded */
    font-family: inherit;
    font-size: 1rem;
    color: ${({ theme }) => theme.colors.text};
    background-color: ${({ theme }) => theme.colors.light};
    transition: all ${({ theme }) => theme.transitions.normal} ease-in-out;
    box-shadow: ${({ theme }) => theme.shadows.sm};

    &:focus {
      outline: none;
      border-color: ${({ theme }) => theme.colors.primaryAccent};
      box-shadow: 0 0 0 3px rgba(0, 123, 255, 0.2), ${({ theme }) => theme.shadows.md};
      transform: scale(1.01); /* Subtle growth on focus */
    }
    &::placeholder {
      color: ${({ theme }) => theme.colors.textSecondary};
      opacity: 0.7;
    }
    &:hover {
      border-color: ${({ theme }) => theme.colors.textSecondary};
      box-shadow: ${({ theme }) => theme.shadows.md};
    }
  }

  textarea {
    resize: vertical;
    min-height: 100px;
  }

  /* Images & Media */
  img {
    max-width: 100%;
    height: auto;
    display: block;
    object-fit: cover;
    border-radius: ${({ theme }) => theme.borderRadius.md}; /* Rounded images */
    transition: transform ${({ theme }) => theme.transitions.normal} ease-out,
                box-shadow ${({ theme }) => theme.transitions.normal} ease-out;

    &:hover {
      transform: scale(1.05); /* More zoom effect */
      box-shadow: ${({ theme }) => theme.shadows.lg}; /* Deeper shadow on hover */
    }

    /* Animated image border (subtle) */
    &.bordered-animated-image {
      border: 3px solid transparent;
      background-clip: padding-box;
      background-origin: border-box;
      background-image: ${({ theme }) => theme.colors.gradientVibrant};
      animation: ${gradientBackground} ${({ theme }) => theme.animations.gradientCycleDuration} linear infinite;
      background-size: 200% 200%;
    }

    /* Example: Apply float to an image */
    &.floating-image {
        animation: ${float} 3s ease-in-out infinite;
    }
  }

  /* Utility Classes (for applying animations and styles) */
  .container {
    max-width: 1280px; /* Slightly wider container */
    margin: 0 auto;
    padding: 0 ${({ theme }) => theme.spacing.lg};
  }

  .text-center {
    text-align: center;
  }

  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border-width: 0;
  }

  /* Interactive Cards/Elements */
  .card {
    background-color: ${({ theme }) => theme.colors.light};
    border-radius: ${({ theme }) => theme.borderRadius.xl}; /* Very rounded cards */
    box-shadow: ${({ theme }) => theme.shadows.md};
    padding: ${({ theme }) => theme.spacing.xl};
    margin-bottom: ${({ theme }) => theme.spacing.xxl};
    perspective: 1000px; /* For 3D tilt effect */
    transform-style: preserve-3d;
    transition: all ${({ theme }) => theme.transitions.normal} ${({ theme }) => theme.transitions.easeOutQuint};
    will-change: transform, box-shadow;

    &:hover {
      transform: translateY(-10px) rotateX(5deg) rotateY(-5deg); /* More dramatic lift and tilt */
      box-shadow: ${({ theme }) => theme.shadows.xxl}; /* Grandest shadow on hover */
      position: relative; /* For z-index stacking on hover */
      z-index: ${({ theme }) => theme.zIndex.base + 1};
    }
    &:active {
      transform: translateY(-2px) rotateX(0deg) rotateY(0deg);
      box-shadow: ${({ theme }) => theme.shadows.lg};
    }

    &.animated-card {
      animation: ${fadeInScale} ${({ theme }) => theme.animations.fadeInDuration} ${({ theme }) => theme.transitions.easeOutQuint} forwards;
      opacity: 0;
      transform: scale(0.9);
      /* Add specific animation-delay as a prop or in a component */
    }
  }

  /* Placeholder for Loading States / Skeleton Screens */
  .skeleton-line {
    background-color: ${({ theme }) => theme.colors.border};
    border-radius: ${({ theme }) => theme.borderRadius.sm};
    height: 1rem;
    margin-bottom: ${({ theme }) => theme.spacing.sm};
    width: 100%;
    overflow: hidden;
    position: relative;
    &::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
      animation: ${shimmer} 1.5s infinite linear; /* Fixed: shimmer applied */
    }
  }

  /* Scroll-to-Top Button or other fixed elements */
  .fixed-bottom-right {
    position: fixed;
    bottom: ${({ theme }) => theme.spacing.xl};
    right: ${({ theme }) => theme.spacing.xl};
    z-index: ${({ theme }) => theme.zIndex.sticky};
    animation: ${bounce} 2s infinite ${({ theme }) => theme.transitions.ease}; /* Fixed: bounce applied */
    box-shadow: ${({ theme }) => theme.shadows.lg};
    border-radius: ${({ theme }) => theme.borderRadius.full};
    padding: ${({ theme }) => theme.spacing.md};
    background-color: ${({ theme }) => theme.colors.primary};
    color: ${({ theme }) => theme.colors.light};
  }

  /* New: A general animated background class (e.g., for hero sections) */
  .animated-hero-bg {
    background: ${({ theme }) => theme.colors.gradientVibrant};
    background-size: 400% 400%; /* Even larger for more movement */
    animation: ${gradientBackground} ${({ theme }) => theme.animations.gradientCycleDuration} ease infinite; /* Fixed: gradientBackground applied */
    position: relative;
    overflow: hidden;
  }

  /* New: Animated Scroll Indicator */
  .scroll-indicator-bar {
    position: fixed;
    top: 0;
    left: 0;
    height: 4px; /* Thickness of the bar */
    background: ${({ theme }) => theme.colors.secondary};
    width: 0%; /* Will be controlled by JS */
    z-index: ${({ theme }) => theme.zIndex.sticky};
    transition: width 0.1s linear; /* Smooth fill */
  }

  /* Custom Scrollbar (for webkit browsers) */
  ::-webkit-scrollbar {
    width: 12px; /* Thicker scrollbar */
    height: 12px;
  }
  ::-webkit-scrollbar-track {
    background: ${({ theme }) => theme.colors.background};
    border-radius: 10px;
  }
  ::-webkit-scrollbar-thumb {
    background: ${({ theme }) => theme.colors.primaryAccent};
    border-radius: 10px;
    border: 3px solid ${({ theme }) => theme.colors.background};
    &:hover {
      background: ${({ theme }) => theme.colors.primaryDark};
    }
  }

  /* Media Queries for Responsive Design */
  @media (max-width: ${({ theme }) => theme.breakpoints.lg}) {
    h1 { font-size: 3rem; }
    h2 { font-size: 2.4rem; }
    .card { padding: ${({ theme }) => theme.spacing.lg}; }
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.md}) {
    h1 { font-size: 2.5rem; }
    h2 { font-size: 2rem; }
    h3 { font-size: 1.8rem; }
    body::before, body::after {
      width: 250px;
      height: 250px;
      filter: blur(80px);
    }
  }

  @media (max-width: ${({ theme }) => theme.breakpoints.sm}) {
    html { font-size: 15px; }
    h1 { font-size: 2rem; }
    h2 { font-size: 1.75rem; }
    button {
      padding: ${({ theme }) => theme.spacing.sm} ${({ theme }) => theme.spacing.md};
      font-size: 0.9rem;
    }
    .fixed-bottom-right {
      bottom: ${({ theme }) => theme.spacing.md};
      right: ${({ theme }) => theme.spacing.md};
    }
  }
`;

export default GlobalStyle;
